  return (
      <Container className="filmSelected">
        <h1 style={{textAlign: "center", margin: "2pc"}}> Films </h1>
        <Row>
          <Col sm = {1}/>
          <Col sm = {3}><Image  src={cassete} fluid /></Col>
          <Col sm = {1}/>
          <Col>

            <Row className="align-items-center"> 
              <GradeHeader data={10}/> 
            </Row>

            <Row> <Grade/></Row>

          </Col>
