}

//singular film rendering
function Film() {
  const [list, setList] = useState(Object);

  const { id } = useParams();
  
  useEffect(() => { 
    axios.get("http://localhost:8080/api/film/" + id)
    .then(res => {
      console.log(res.data)
      setList(res.data)
    })
    .catch(err => {
      console.log(err)
    })
  }, [setList]);

  useEffect(() => {
    console.log({ list });
  }, [list]);
  
  return (
    <Dumm></Dumm>
    //<FilmDiv data={list}/>
  )
}

export default Film;
