import React, { Component, useEffect, useState } from "react";
import { Row } from "react-bootstrap";
import { useParams } from "react-router-dom";

function FilmDiv(props) {
    return (
        <div>
            <Row>{props.data.avgGrade}</Row>
            <Row>{props.data.title}</Row>
            <Row>{props.data.premiereDate}</Row>
            <Row>{props.data.description}</Row>
        </div>
    )
  }

//singular film rendering
function Film() {
  const [list, setList] = useState([]);

  let { id } = useParams();
  
  useEffect(function effectFunction() {
    fetch("http://localhost:8080/api/film/" + id)
    .then(response => response.json())
    .then(({ data: list }) => { setList(list); });
  }, []);
  
  return (
      <div>
        list.map(film => <FilmDiv key={film.id} data={film}/> )
      </div>  
  )
}

export default Film;
