import React, { Component, useEffect, useState, initialState } from "react";
import { Container, Row, Col, Image  } from "react-bootstrap";
import { useParams } from "react-router-dom";
import axios from 'axios'
import cassete from './casette.png';
import Grade from "./Grade";

const GradeHeader = (props) => {
  return (
      <div className="gradeHeader">    {console.log(props.data)}

          <div className="circle"><p>{props.data}</p></div>
      </div>
  )
}

function FilmDiv(props) {
  return (
    <Container className="filmSelected">
      {console.log(props)}
      <h1 style={{textAlign: "center", margin: "2pc"}}> fsad </h1>
      <Row>
        <Col sm = {1}/>
        <Col sm = {4}><Image  src={cassete} fluid /></Col>
        <Col sm = {1}/>
        <Col>
            <Row> 
              <GradeHeader data={10}/> 
            </Row>

            <Row> 
              <Grade/> 
            </Row>
          
        </Col>
        
      </Row>
      <Row><h4 style={{ marginTop: "3vh"}}>Date of premiere: {1}</h4></Row>
      <Row>
        <div className="desc">
          {10}
        </div>
      </Row>

    </Container>
)
}

function Dumm(props) {
  return (
      <Container className="filmSelected">
        <h1 style={{textAlign: "center", margin: "2pc"}}> Films </h1>
        <Row>
          <Col sm = {1}/>
          <Col sm = {4}><Image  src={cassete} fluid /></Col>
          <Col sm = {1}/>
          <Col>
              <Row> 
                <GradeHeader data={10}/> 
              </Row>

              <Row> 
                <Grade/> 
              </Row>
            
          </Col>
          
        </Row>
        <Row><h4 style={{ marginTop: "3vh"}}>Date of premiere: 1999</h4></Row>
        <Row><div className="desc">Mr. Nobody is a 2009 science fiction drama film written and directed by Jaco Van Dormael and starring Jared Leto, Sarah Polley, Diane Kruger, Linh Dan Pham, Rhys Ifans, Natasha Little, Toby Regbo and Juno Temple. The film tells the life story of Nemo Nobody, a 118-year-old man who is the last mortal on Earth after the human race has achieved quasi-immortality. Nemo, memory fading, refers to his three main loves and to his parents' divorce and subsequent hardships endured at three critical junctions in his life: at age nine, fifteen, and thirty-four. Alternate life paths branching out from each of those critical junctions are examined. The speculative narrative often changes course with the flick of a different possible decision at each of those ages. The film uses nonlinear narrative and the multiverse hypothesis style.

                Mr. Nobody had its world premiere at the 66th Venice International Film Festival where it received the Golden Osella and the Biografilm Lancia Award. Critical response was generally strong and the film was nominated for seven Magritte Awards, winning six, including Best Film and Best Director for Van Dormael. The film was mostly funded through European financiers and was released in Belgium on 13 January 2010. Since its original release, Mr. Nobody has become a cult film, noted for its philosophy and cinematography, personal characters and Pierre Van Dormael's soundtrack
        </div>
        </Row>

      </Container>
  )
}

//singular film rendering
function Film() {
  const [list, setList] = useState(Object);

  const { id } = useParams();
  
  useEffect(() => { 
    axios.get("http://localhost:8080/api/film/" + id)
    .then(res => {
      console.log(res.data)
      setList(res.data)
    })
    .catch(err => {
      console.log(err)
    })
  }, [setList]);

  useEffect(() => {
    console.log({ list });
  }, [list]);
  
  return (
    <FilmDiv data={list}/>
  )
}

export default Film;
