import React, { Component } from "react";
import { Row } from "react-bootstrap";
import { useParams } from "react-router-dom";

function FilmDiv(props) {
    return (
        <div>
            <Row>{props.data.avgGrade}</Row>
            <Row>{props.data.title}</Row>
            <Row>{props.data.premiereDate}</Row>
            <Row>{props.data.description}</Row>
        </div>
    )
  }

//singular film rendering
function Film() {
  state = {
      film: []
  }
  let { id } = useParams();
  
  fetch("http://localhost:8080/api/film/" + id)
  .then(response => response.json())
  .then(data => this.setState({film: data}))
  
  return (
      <Row>{state.id}</Row>
  )
}

export default Film;
