import React, { Component } from 'react';
import { Container, Row, Col } from "react-bootstrap";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

import Film from "./Film.jsx"
//multiple films rendering in a list

function FilmDiv(props) {
  return (
    <Router>
      <Link to="/film">
      <Row>
          {console.log(props.data)}
          <Col>{props.data.avgGrade}</Col>
          <Col>{props.data.title}</Col>
          <Col>{props.data.premiereDate}</Col>
          <Col>{props.data.description}</Col>
      </Row>
      </Link>

      <Routes>
            <Route exact path="/film" element={ <Film data={props.data.id}/> } />
      </Routes>
    </Router>
)
}

class FilmsContainer extends Component{
  state = {
    list: []
  }

  componentDidMount() {
    fetch("http://localhost:8080/api/film/ranking/0")
    .then(response => response.json())
    .then(data => {
      this.setState({list: data})
    })
    .catch(rejected => {
      console.log(rejected);
    });
  }

  render() {  
    return (
      <div className="dataDiv">
        {this.state.list.map(film => 
        <Router>
        <FilmDiv key={film.id} data={film}/>
        </Router>
        )}    
      </div>
    )}
}

function Films() {
  return (
    <Container fluid>
      <h1>Films</h1>
      <FilmsContainer/>
    </Container>
  )
}


export default Films;
