import { useEffect, useState } from "react"

const Stars = () => {
    
}

const Grade = () => {
    const [grade, setGrade] = useState(0);
    const [showR, setShowR] = useState(false);

    const handleOptionChange = (event) => {
        let val = event.target.value
        setGrade(val)

        let stars = document.getElementById("starsRadio").elements
        console.log(stars)
    }

    const handleHover = (event) => {
        console.log("E")

        for(let i = 1; i <= event.target.value; i++) {
            console.log("E")
        }
    }

    return (
        <div class="stars">
            <span>{grade}</span>
        <form id="starsRadio">
            <input type="radio" name="radio" id="radio1" value={1} onClick={handleOptionChange}/>
            <label for="radio1"><span class="star"></span></label>

            <input type="radio" name="radio" id="radio2" value={2} onChange={handleOptionChange}/>
            <label for="radio2" onmouseover={handleHover}><span class="star"></span></label>

            <input type="radio" name="radio" id="radio3" value={3} onChange={handleOptionChange}/>
            <label for="radio3" onmouseover={handleHover}><span class="star"></span></label>

            <input type="radio" name="radio" id="radio4" value={4} onChange={handleOptionChange}/>
            <label for="radio4" onmouseover={handleHover}><span class="star"></span></label>

            <input type="radio" name="radio" id="radio5" value={5} onChange={handleOptionChange}/>
            <label for="radio5" onmouseover={handleHover}><span class="star"></span></label>
        </form>
        </div>
    )
}

export default Grade;
