import React, { Component, useEffect, useState, initialState } from "react";
import { Row } from "react-bootstrap";
import { useParams } from "react-router-dom";

function FilmDiv(props) {
    return (
        <div>
            <Row>{props.data.avgGrade}</Row>
            <Row>{props.data.title}</Row>
            <Row>{props.data.premiereDate}</Row>
            <Row>{props.data.description}</Row>
        </div>
    )
  }

//singular film rendering
function Film() {
  const [list, setList] = useState( [] );

  let { id } = useParams();
  
  useEffect(() => { 
    const fetchData = async() => {
      const response = await fetch("http://localhost:8080/api/film/" + id);
      const json = await response.json();
      setList(json);
    }
    fetchData();
    console.log(list);
  }, [setList]);
  
  return (
      <ul>
          {list.map(film => {<FilmDiv key={film.id} data={film}/>} )}    
      </ul>  
  )
}

export default Film;
