import React, { Component } from "react";
import { useParams } from "react-router-dom";
import { Row } from "react-bootstrap";

function FilmDiv(props) {
    return (
        <div>
            <Row>{props.data.avgGrade}</Row>
            <Row>{props.data.title}</Row>
            <Row>{props.data.premiereDate}</Row>
            <Row>{props.data.description}</Row>
        </div>
    )
  }

//singular film rendering
class FilmsContainer extends Component{
    state = {
        list: []
    }
  
    componentDidMount() {
        fetch("http://localhost:8080/api/film/" + this.props.match.params.id)
        .then(response => response.json())
        .then(data => { this.setState( {list: data} ) })
        .catch(rejected => {
        console.log(rejected); });
    }
  
    render() {  
      return (
        <div className="dataDiv">
          {this.state.list.map(film => <FilmDiv key={film.id} data={film}/> )}    
        </div>
      )
    }
}

export default Film;
